export const initialState = {
  basket: [
    {
      id: "0007",
      title:
        'LG 29WK600-W 29" UltraWide 21:9 WFHD (2560 x 1080) IPS Monitor with HDR10 and FreeSyncLG 29WK600-W 29" UltraWide 21:9 WFHD (2560 x 1080) IPS Monitor with HDR10 and FreeSync',
      price: 889.89,
      rating: 1,
      image: "https://i.pinimg.com/originals/fe/fa/c5/fefac54600198b1ccd4bb88e13d3e47d.jpg",
    },
  ],
  user: null,
};

function reducer(state, action) {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_BASKET":
      return {
        ...state,
        basket: [...state.basket, action.payload],
      };
    case "REMOVE_FROM_BASKET":
      break;
    default:
      return state;
  }
}

export default reducer;
