import React from "react";
import "./Product.css";

const Product = ({ id, title, price, rating, image }) => {
  return (
    <div className="product">
      <p>{title}</p>
      <p className="product__price">
        <small>$</small>
        <strong>{price}</strong>
      </p>
      <div className="product__rating">
        {Array(rating)
          .fill()
          .map((_) => (
            <span role="img" aria-label="Star">
              ⭐️
            </span>
          ))}
      </div>
    </div>
  );
};

export default Product;
